import { useState } from "react";
import {
  Navbar as ReactNavbar,
  Container,
  Offcanvas,
} from "react-bootstrap";
import "./style2.css";
import { Link, useNavigate } from "react-router-dom";

function CustomNavbar() {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const navigate = useNavigate();

  const handleAboutClick = () => {
    // Navigate to homepage
    navigate("/");
    // Small delay to ensure page loads before scrolling
    setTimeout(() => {
      const testimonials = document.getElementById("testimonials");
      if (testimonials) {
        testimonials.scrollIntoView({ behavior: "smooth" });
      }
    }, 300);
    handleClose();
  };

  return (
    <ReactNavbar
      bg="dark"
      variant="dark"
      expand={false}
      fixed="top"
      className="bg-transparent border-0 shadow-none"
    >
      <Container fluid className="p-0 m-0">
        {/* Toggle button */}
        <ReactNavbar.Toggle onClick={handleShow} className="ms-auto" />

        <Offcanvas
          show={show}
          onHide={handleClose}
          id="offcanvasDarkNavbar"
          placement="end"
          className="text-bg-dark"
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title style={{ fontSize: "3rem", fontWeight: "bold" }}>
              TEDX
            </Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <ul className="list-unstyled">
              <li>
                <Link className="animated-text" to="/" onClick={handleClose}>
                  Home
                </Link>
              </li>
              <li>
                {/* Custom handler for About Us */}
                <button
                  className="animated-text btn btn-link p-0 text-decoration-none"
                  onClick={handleAboutClick}
                >
                  About us
                </button>
              </li>
              <li>
                <Link
                  className="animated-text"
                  to="/sponsor"
                  onClick={handleClose}
                >
                  Sponsor
                </Link>
              </li>
              <li>
                <Link
                  className="animated-text"
                  to="/team"
                  onClick={handleClose}
                >
                  Team
                </Link>
              </li>
              <li>
                <Link
                  className="animated-text"
                  to="/speakers"
                  onClick={handleClose}
                >
                  Speakers
                </Link>
              </li>
            </ul>
          </Offcanvas.Body>
        </Offcanvas>
      </Container>
    </ReactNavbar>
  );
}

export default CustomNavbar;
